// store/todosSlice.js
import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

export const fetchTodos = createAsyncThunk("todo/fetchTodos", async () => {
  const res = await fetch("http://localhost:3001/todos");
  return await res.json();
});

export const addTodo = createAsyncThunk("todo/addTodo", async (newTodo) => {
  const res = await fetch("http://localhost:3001/todos", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(newTodo),
  });
  return await res.json();
});

export const toggleTodo = createAsyncThunk("todo/toggleTodo", async (todo) => {
  const res = await fetch(`http://localhost:3001/todos/${todo.id}`, {
    method: "PATCH",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ completed: !todo.completed }),
  });
  return await res.json();
});

export const deleteTodo = createAsyncThunk("todo/deleteTodo", async (id) => {
  await fetch(`http://localhost:3001/todos/${id}`, {
    method: "DELETE",
  });
  return id;
});

const todoSlice = createSlice({
  name: "todos",
  initialState: [],
  extraReducers: (builder) => {
    builder
      .addCase(fetchTodos.fulfilled, (_, action) => action.payload)
      .addCase(
        addTodo.fulfilled,
        (state, action) => void state.push(action.payload)
      )
      .addCase(toggleTodo.fulfilled, (state, action) =>
        state.map((todo) =>
          todo.id === action.payload.id ? action.payload : todo
        )
      )
      .addCase(deleteTodo.fulfilled, (state, action) =>
        state.filter((todo) => todo.id !== action.payload)
      );
  },
});

export default todoSlice.reducer;
